mapper.update:
UPDATE NOTICE SET 
         N_TITLE=#{n_title}
       , N_CONTS=#{n_conts}
       , N_REGDATE=SYSDATE
WHERE N_SEQ=#{n_seq}

mapper.delete:
DELETE FROM NOTICE 
    WHERE N_SEQ IN

	#{n_seq}

mapper.update:
UPDATE QA SET 
      Q_TITLE=#{q_title}
    , Q_CONTS=#{q_conts}
    , Q_REGDATE=SYSDATE
   WHERE Q_SEQ=#{q_seq}
